import React from "react";
import styled from 'styled-components';
import Calendar from 'react-calendar';
import './Date.css';

const Container=styled.div`
  display:flex;
  flex-direction:column;
`;

function check(e){
  e.value=""
}
 




class Date extends React.Component {

  onClickDay=u=>{
    let i;
    let v;
    if(String(u).length==1){
      v=u;
    }
    else{
     v=String(u).slice(8,10);
     if(v.slice(0,1)=="0"){
       v=v.slice(1,2);
      }
    }
    var tile=document.getElementsByClassName("react-calendar__tile");
    for(i=0;i<tile.length;i++){
      if(tile[i].firstChild.innerHTML==v){
        console.log(tile[i].firstChild.innerHTML);
        tile[i].firstChild.innerHTML="d";
        
      }
    }
  }

  render() {
    return (
      <Container>
        <Calendar onClickDay={this.onClickDay}
         className="calendar"></Calendar>
      </Container>
    );
  }
}
export default Date;
