import React from "react";
import Banner from "../components/Banner";
import Preview from "../components/Preview";
import MovieContainer from "../components/MovieContainer";
import axios from "axios";

class Main extends React.Component {
  constructor(props) {
    super(props);
    this.state={
      movies:undefined
    }
  }

  componentDidMount(){
    const response=axios.get("https://api.themoviedb.org/3/movie/popular?api_key=a36305ddf529faa0c37acbf47e633d08&language=ko-kr")
    .then(response =>{
      console.log(response);
      this.setState({movies: response.data.results})
    })
    .catch(err=>{
      console.log("err!!",err);
    })
  }

  render() {
    return (
      <div style={{marginTop:"43%",overflowX:"hidden",width:"100%"}}>
        <Banner />
        <Preview />
        <MovieContainer title="TV프로그램" movies={this.state.movies} />
        <MovieContainer title="드라마" movies={this.state.movies} />
        <MovieContainer title="드라마" movies={[{title: "인생"},{title: "타인은 지옥이다"},{title: "동백꽃 필 무렵"}]} />
        <MovieContainer title="드라마" movies={[{title: "인생"},{title: "타인은 지옥이다"},{title: "동백꽃 필 무렵"}]} />
        <MovieContainer title="드라마" movies={[{title: "인생"},{title: "타인은 지옥이다"},{title: "동백꽃 필 무렵"}]} />
        <MovieContainer title="드라마" movies={[{title: "인생"},{title: "타인은 지옥이다"},{title: "동백꽃 필 무렵"}]} />
        <MovieContainer title="드라마" movies={[{title: "인생"},{title: "타인은 지옥이다"},{title: "동백꽃 필 무렵"}]} />
        <MovieContainer title="드라마" movies={[{title: "인생"},{title: "타인은 지옥이다"},{title: "동백꽃 필 무렵"}]} />
        <MovieContainer title="드라마" movies={[{title: "인생"},{title: "타인은 지옥이다"},{title: "동백꽃 필 무렵"}]} />
        <MovieContainer title="드라마" movies={[{title: "인생"},{title: "타인은 지옥이다"},{title: "동백꽃 필 무렵"}]} />
        <MovieContainer title="드라마" movies={[{title: "인생"},{title: "타인은 지옥이다"},{title: "동백꽃 필 무렵"}]} />
      </div>
    );
  }
}

export default Main;
