import React from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";

const Wrapper = styled.div`
  width: 100%;
  height: 68px;
  display: flex;
  padding: 20px 58px;
  box-sizing: border-box;
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  z-index: 9000;
`;

const Logo = styled.img`
  height: 100%;
  margin-right: 30px;
`;

const NavText = styled.ul`
  display: flex;
  align-items: center;
  flex: 1;
`;
const NavTextItem = styled.li`
  color: white;
  list-style: none;
  margin-right: 10px;
  &:hover {
    font-weight: normal;
    color: #b3b3b3;
  }
`;
const NavIcon = styled.ul`
  display: flex;
`;
const NavIconItem = styled.li`
  height: 100%;
  width: fit-content;
  list-style: none;
  margin-right: 3px;
`;
const Img = styled.img`
  list-style: none;
  height: 100%;
`;
const setFont = event => {
  let asd = document.getElementsByClassName("asd");
  for (let a = 0; a < asd.length; a++) {
    asd[a].style.fontWeight = "normal";
    asd[a].style.color = "#e5e5e5";
  }
  event.target.style.fontWeight = "bold";
  event.target.style.color = "#fff";
};
const Banner = props => {
  return (
    <>
      <Wrapper>
        <NavText>
          <NavTextItem className="asd" onClick={setFont}>
            <Link to="/">1person</Link>
          </NavTextItem>
          <NavTextItem className="asd" onClick={setFont}>
            <Link to="/2p">2person</Link>
          </NavTextItem>
        </NavText>
      </Wrapper>
    </>
  );
};
export default Banner;
