import React from "react";
import styled from 'styled-components';
import Calendar from 'react-calendar';
import './Date.css';

const Container=styled.div`
  display:flex;
  flex-direction:column;
`;

function check(e){
  e.value=""
}
 




class Date extends React.Component {

  onClickDay=u=>{
    let i;
    let v;
    let ch;
    console.log(u);
     v=String(u).slice(8,10);
     if(v.slice(0,1)=="0"){
       v=v.slice(1,2);
      }
    var tile=document.getElementsByClassName("react-calendar__tile");
    for(i=0;i<tile.length;i++){
      if(tile[i].firstChild.innerHTML==v){
        ch=tile[i].firstChild;
      }
    }
    if(ch.check==true){
      ch.check=false;
    }
    else{
      ch.check=true;
    }
    console.log(ch.check);
  }

  render() {
    return (
      <Container>
        <Calendar onClickDay={this.onClickDay}
         className="calendar"></Calendar>
      </Container>
    );
  }
}
export default Date;
