import React from "react";
import Preview from "../components/Preview";
import MovieContainer from "../components/MovieContainer";
import styled from "styled-components";
import axios from "axios";
import Banner from "../components/Banner";
import withLayout from "../components/withLayout";

const MovieContainerWrapper = styled.div`
  & > *:not(:last-child) {
    margin-bottom: 25px;
  }
`;
class Tv extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      movies: [],
      movies2 : []
    };
  }

  componentDidMount() {
      axios
      .get(
        "https://api.themoviedb.org/3/tv/popular?api_key=a36305ddf529faa0c37acbf47e633d08&language=ko-kr"
      )
      .then(response => {
        console.log(response);
        this.setState({ tv: response.data.results });
      })
      .catch(err => {
        console.log("err!!", err);
      });
      axios
      .get(
        "https://api.themoviedb.org/3/trending/tv/week?api_key=a36305ddf529faa0c37acbf47e633d08&language=ko-kr"
      )
      .then(response => {
        console.log(response);
        this.setState({ tv2: response.data.results });
      })
      .catch(err => {
        console.log("err!!", err);
      });
  }

  render() {
    return (
      <>
        <Preview />
        <MovieContainerWrapper
          style={{ marginTop: "45%", overflowX: "hidden", overflowY:"hidden", width: "100%" }}
        >
           <MovieContainer title="주간 인기" movies={this.state.tv} />
          <MovieContainer title="인기 TV프로그램" movies={this.state.tv2} />
         
        </MovieContainerWrapper>
      </>
    );
  }
}
export default withLayout(Tv);
