import * as React from "react";
import styled from "styled-components"

const Wrapper = styled.div`
    color:white;
    width:calc(20% - 8px);
    padding-bottom:10%;
    margin-right:10px;
    height:0;
    position:relative;
    transition:0.2s;
    &>div{
        position:absolute;
        left:0;
        right:0;
        top:0;
        bottom:0;
        display:flex;
        justify-content:center;
        align-items:center;
    }
    &:hover{
        transform: scale(1.1);
        z-index:100;
    }
`

const MovieItem = (props) => {
    return (
        <Wrapper>
            <img src={props.imgs} width="100%" padding-botton="100%" />
            <div>{props.title}</div>
        </Wrapper>
    )
}

export default MovieItem;
