import React from "react";
import styled from 'styled-components';
import Calendar from 'react-calendar';
import './Date.css';

const Container=styled.div`
  display:flex;
  flex-direction:column;
`;

function check(e){
  e.value=""
}



class Date extends React.Component {
  constructor(props) {
    super(props);
    this.state ={
      name:'',
      phone:'',
      toggle:false,
  };
  
  
  }
  
  handlingEdit = (e)=>{
    e.preventDefault();
    
    this.setState({
      toggle: !this.state.toggle
   });
};

  ifx = i => {
    this.setState({
      toggle: !this.state.toggle
    });
  }


  onClickDay=u=>{
    let i;
    let v;
    let ch;
    console.log(u);
     v=String(u).slice(8,10);
     if(v.slice(0,1)=="0"){
       v=v.slice(1,2);
      }
    var tile=document.getElementsByClassName("react-calendar__tile");
    for(i=0;i<tile.length;i++){
      if(tile[i].firstChild.innerHTML==v||tile[i].firstChild.innerHTML=="X"){
        ch=tile[i].firstChild;
        break;
      }
    }
    console.log(ch.check);
    if(ch.check==false){
      ch.check=true;
      ch.innerHTML=v;
      this.ifnx(i);
    }
    
    else if(ch.check==true){
      ch.check=false;
      ch.innerHTML="X";
      this.ifx(i);
    }
    else if(ch.check==undefined){
      ch.check=false;
      ch.innerHTML="X";
      this.ifx(i);
    }
    console.log(this.state.checked);
  }

  render() {
    const {dataInformation} = this.props;
    let {toggle} = this.state;
    return(
    <form style={this.style}>
        {toggle? <div>
                <input value={this.state.name} name={'name'} onChange={this.handlingChange}/><br/>
                <input value={this.state.phone} name={'phone'} onChange={this.handlingChange}/><br/>
            </div>:
            <div>
                {dataInformation.name} <br/>
                {dataInformation.phone}<br/>
                </div>}
        <button onClick={this.handlingDelete}>삭제</button><button onClick={this.handlingEdit}>수정</button>
      </form>)

      // <Container>
      //   <Calendar className="calendar" onClickDay={this.onClickDay}/>
      // </Container>
    
  }
}
export default Date;
