import React from "react";
import styled from 'styled-components';
import Calendar from 'react-calendar';
import './Date.css';

const Container=styled.div`
  display:flex;
  flex-direction:column;
`;

function check(e){
  e.value=""
}
 




class Date extends React.Component {
  state = {
    date: new Date(),
  }
 
  onChange = date => this.setState({ date })

  render() {
    return (
      <Container>
        <Calendar onClickDay="check" onChange={this.onChange} value={this.state.date} className="calendar"></Calendar>
      </Container>
    );
  }
}
export default Date;
