import * as React from "react";
import styled from "styled-components";
import MovieItem from "./MovieItem";

const BtnSize="45px";

const Wrapper=styled.div`
    color:white;
    width:clac(100%);

    .title{
        font-weight:bold;
    }
    .container{
        position:relative;
    }
    .slider{
        display:flex;
        flex-direction:row;
    }
`;




const BtnLeft=styled.button`
    width:${BtnSize};
    height:100%;
    position:absolute;
    right:100%;
    z-index:5;
    opacity:0;
    border:none;
    color:white;
    outline:none;

    &:hover{
        opacity:1;
        background-color:rgba(0,0,0,0.5);
    }
`;

const BtnRight=styled.button`
    left:100%;
    right:none;
`;

const MovieContainer=props=>{
    return <Wrapper>
        <div className="title">TV프로그램 ㆍ 코미디</div>
        <div className="container">
            <BtnLeft> {"<"}</BtnLeft>
            <BtnRight>{">"}</BtnRight>
            <div className="slider">
                {[...Array(15).keys()].map(value=>(
                    <MovieItem/>
                ))}
            </div>
        </div>
    </Wrapper>
}

export default MovieContainer
